(function(){
  function toggle(ev) {
    var button = ev.target;
    var parent = ev.target.parentElement;
    while(parent) {
      if (parent.tagName === 'TABLE' && parent.classList.contains('summary')) break;
      parent = parent.parentElement;
    }

    if (!parent) return;

    var tbody = parent.querySelector('tbody');
    if (button.classList.contains('opened')) {
      button.classList.remove('opened');
      button.classList.add('closed');
      tbody.style.display = 'none';
    } else {
      button.classList.remove('closed');
      button.classList.add('opened');
      tbody.style.display = 'block';
    }
  }

  var buttons = document.querySelectorAll('.inherited-summary thead .toggle');
  for (var i = 0; i < buttons.length; i++) {
    buttons[i].addEventListener('click', toggle);
  }
})();

// inner link(#foo) can not correctly scroll, because page has fixed header,
// so, I manually scroll.
(function(){
  var matched = location.hash.match(/errorLines=([\d,]+)/);
  if (matched) return;

  function adjust() {
    window.scrollBy(0, -55);
    var el = document.querySelector('.inner-link-active');
    if (el) el.classList.remove('inner-link-active');

    // ``[ ] . ' " @`` are not valid in DOM id. so must escape these.
    var id = location.hash.replace(/([\[\].'"@$])/g, '\\$1');
    var el = document.querySelector(id);
    if (el) el.classList.add('inner-link-active');
  }

  window.addEventListener('hashchange', adjust);

  if (location.hash) {
    setTimeout(adjust, 0);
  }
})();

(function(){
  var els = document.querySelectorAll('[href^="#"]');
  var href = location.href.replace(/#.*$/, ''); // remove existed hash
  for (var i = 0; i < els.length; i++) {
    var el = els[i];
    el.href = href + el.getAttribute('href'); // because el.href is absolute path
  }
})();

(function(){
  var matched = location.pathname.match(/\/(manual\/.*\.html)$/);
  if (!matched) return;

  var currentName = matched[1];
  var cssClass = '.navigation .manual-toc li[data-link="' + currentName + '"]';
  var styleText = cssClass + '{ display: block; }\n';
  styleText += cssClass + '.indent-h1 a { color: #039BE5 }';
  var style = document.createElement('style');
  style.textContent = styleText;
  document.querySelector('head').appendChild(style);
})();

(function(){
  if (location.protocol === 'file:') {
    var elms = document.querySelectorAll('a[href="./"]');
    for (var i = 0; i < elms.length; i++) {
      elms[i].href = './index.html';
    }
  }
})();

(function(){
  prettyPrint();
  var lines = document.querySelectorAll('.prettyprint.linenums li[class^="L"]');
  for (var i = 0; i < lines.length; i++) {
    lines[i].id = 'lineNumber' + (i + 1);
  }

  var matched = location.hash.match(/errorLines=([\d,]+)/);
  if (matched) {
    var lines = matched[1].split(',');
    for (var i = 0; i < lines.length; i++) {
      var id = '#lineNumber' + lines[i];
      var el = document.querySelector(id);
      el.classList.add('error-line');
    }
    return;
  }

  if (location.hash) {
    // ``[ ] . ' " @`` are not valid in DOM id. so must escape these.
    var id = location.hash.replace(/([\[\].'"@$])/g, '\\$1');
    var line = document.querySelector(id);
    if (line) line.classList.add('active');
  }
})();

(function(){
  var searchIndex = window.esdocSearchIndex;
  var searchBox = document.querySelector('.search-box');
  var input = document.querySelector('.search-input');
  var result = document.querySelector('.search-result');
  var selectedIndex = -1;
  var prevText;

  // active search box and focus when mouse enter on search box.
  searchBox.addEventListener('mouseenter', function(){
    searchBox.classList.add('active');
    input.focus();
  });

  // search with text when key is upped.
  input.addEventListener('keyup', function(ev){
    var text = ev.target.value.toLowerCase();
    if (!text) {
      result.style.display = 'none';
      result.innerHTML = '';
      return;
    }

    if (text === prevText) return;
    prevText = text;

    var html = {class: [], method: [], member: [], function: [], variable: [], typedef: [], external: [], file: [], test: [], testFile: []};
    var len = searchIndex.length;
    var kind;
    for (var i = 0; i < len; i++) {
      var pair = searchIndex[i];
      if (pair[0].indexOf(text) !== -1) {
        kind = pair[3];
        html[kind].push('<li><a href="' + pair[1] + '">' + pair[2] + '</a></li>');
      }
    }

    var innerHTML = '';
    for (kind in html) {
      var list = html[kind];
      if (!list.length) continue;
      innerHTML += '<li class="search-separator">' + kind + '</li>\n' + list.join('\n');
    }
    result.innerHTML = innerHTML;
    if (innerHTML) result.style.display = 'block';
    selectedIndex = -1;
  });

  // down, up and enter key are pressed, select search result.
  input.addEventListener('keydown', function(ev){
    if (ev.keyCode === 40) {
      // arrow down
      var current = result.children[selectedIndex];
      var selected = result.children[selectedIndex + 1];
      if (selected && selected.classList.contains('search-separator')) {
        var selected = result.children[selectedIndex + 2];
        selectedIndex++;
      }

      if (selected) {
        if (current) current.classList.remove('selected');
        selectedIndex++;
        selected.classList.add('selected');
      }
    } else if (ev.keyCode === 38) {
      // arrow up
      var current = result.children[selectedIndex];
      var selected = result.children[selectedIndex - 1];
      if (selected && selected.classList.contains('search-separator')) {
        var selected = result.children[selectedIndex - 2];
        selectedIndex--;
      }

      if (selected) {
        if (current) current.classList.remove('selected');
        selectedIndex--;
        selected.classList.add('selected');
      }
    } else if (ev.keyCode === 13) {
      // enter
      var current = result.children[selectedIndex];
      if (current) {
        var link = current.querySelector('a');
        if (link) location.href = link.href;
      }
    } else {
      return;
    }

    ev.preventDefault();
  });

  // select search result when search result is mouse over.
  result.addEventListener('mousemove', function(ev){
    var current = result.children[selectedIndex];
    if (current) current.classList.remove('selected');

    var li = ev.target;
    while (li) {
      if (li.nodeName === 'LI') break;
      li = li.parentElement;
    }

    if (li) {
      selectedIndex = Array.prototype.indexOf.call(result.children, li);
      li.classList.add('selected');
    }
  });

  // clear search result when body is clicked.
  document.body.addEventListener('click', function(ev){
    selectedIndex = -1;
    result.style.display = 'none';
    result.innerHTML = '';
  });

})();

(function(){
  function toggle(ev) {
    var button = ev.target;
    var parent = ev.target.parentElement;
    while(parent) {
      if (parent.tagName === 'TR' && parent.classList.contains('test-interface')) break;
      parent = parent.parentElement;
    }

    if (!parent) return;

    var direction;
    if (button.classList.contains('opened')) {
      button.classList.remove('opened');
      button.classList.add('closed');
      direction = 'closed';
    } else {
      button.classList.remove('closed');
      button.classList.add('opened');
      direction = 'opened';
    }

    var targetDepth = parseInt(parent.dataset.testDepth, 10) + 1;
    var nextElement = parent.nextElementSibling;
    while (nextElement) {
      var depth = parseInt(nextElement.dataset.testDepth, 10);
      if (depth >= targetDepth) {
        if (direction === 'opened') {
          if (depth === targetDepth)  nextElement.style.display = '';
        } else if (direction === 'closed') {
          nextElement.style.display = 'none';
          var innerButton = nextElement.querySelector('.toggle');
          if (innerButton && innerButton.classList.contains('opened')) {
            innerButton.classList.remove('opened');
            innerButton.classList.add('closed');
          }
        }
      } else {
        break;
      }
      nextElement = nextElement.nextElementSibling;
    }
  }

  var buttons = document.querySelectorAll('.test-summary tr.test-interface .toggle');
  for (var i = 0; i < buttons.length; i++) {
    buttons[i].addEventListener('click', toggle);
  }

  var topDescribes = document.querySelectorAll('.test-summary tr[data-test-depth="0"]');
  for (var i = 0; i < topDescribes.length; i++) {
    topDescribes[i].style.display = '';
  }
})();

import { PID_Controller } from "./pid-controller"

/**
 * The compass class encapsulates logic around rendering the actual Compass
 * based on device orientation data.
 */
export class Compass {
    /**
     * Creates a compass from a HTML element
     * @param {Element} html HTML element
     */
    constructor(html) {
        this.html = html;
        this.slider = html.querySelector(".compass-slider");
        /**
         * Gets slider width
         * @returns {Number} width
         */
        const setSliderWidth = () => this.WIDTH = this.slider.getBoundingClientRect().width;

        setSliderWidth();
        window.onresize = () => setSliderWidth();

        this.angle = 0;
        this.lastOffset = null;
        this.markerPos = null;
        this.lastAngle = 0;

        this.leftside = true;
        this.pid = new PID_Controller(0.15, 0.04, 0.001);
        this.pid.setOutputLimits(-45, 45);
        window.pid = this.pid;

        /**
         * This interval samples data for the PID controller.
         */
        setInterval(() => {
            if (this.lastAngle > 270 && this.pid.target < 90) {
                console.log("Skip animation right");
                this.slider.style.transition = `none`;
                this.angle = 0;
                window.requestAnimationFrame(() => {
                    this.render();
                    setTimeout(() => {
                        this.slider.style.transition = ``;
                        setTimeout(() => {
                            this.angle += this.pid.compute(this.angle);
                            window.requestAnimationFrame(() => this.render());
                        }, 5);
                    }, 1);
                });
                this.lastAngle = 0;
                return;
            } else if (this.pid.target > 270 && this.lastAngle < 90) {
				console.log("Skip animation left");
				this.slider.style.transition = `none`;
                this.angle = 359.99;
				window.requestAnimationFrame(() => {
                    this.render();
                    setTimeout(() => {
                        this.slider.style.transition = ``;
                        setTimeout(() => {
                            this.angle += this.pid.compute(this.angle);
                            window.requestAnimationFrame(() => this.render());
                        }, 5);
                    }, 1);
				});	
                this.lastAngle = 359;
                return;
            }        

            this.angle += this.pid.compute(this.angle);
            window.requestAnimationFrame(() => this.render());
            this.lastAngle = this.angle;
        }, 40);
    }

    /**
     * Sets rotation of the compass
     * @param {Number} angle rotation (Degrees)
     * @returns {undefined}
     */
    setAngle(angle) {
        this.pid.setTarget(angle);
    }

    /**
     * Sets the POI marker position on the compass
     * @param {Number} angle position (Degrees)
     * @returns {undefined}
     */
    setMarkerPosition(angle) {
        //Shift to correct position... -90 for landscape adjust, -45 because north marker is in half...
        angle -= 135;
        if (angle < 0) {
            angle += 360;
        }
        this.markerPos = angle;

        // Switching north segment when rolling over
        if (!this.leftside) {
            if (this.markerPos < 90) {
                this.renderMarker(this.markerPos, 360);
            } else {
                this.renderMarker(this.markerPos, 0);
            }
        } else if (this.leftside) {
            if (this.markerPos > 270) {
                this.renderMarker(this.markerPos, -360);
            } else {
                this.renderMarker(this.markerPos, 0);
            }
        }
    }

    /**
     * Renders the POI marker on the compass. Offset is used in case compass is near 0
     * @param {Number} angle position (Degrees)
     * @param {Number} offset offset (Degrees)
     * @returns {undefined}
     */
    renderMarker(angle, offset) {
        let precision = Math.pow(10, 3);

        let realAngle = angle % 360;
        realAngle += offset;
        let pos = Math.floor((realAngle / 90 * this.WIDTH) * precision) / precision;

        let marker = this.html.querySelector("#compass-icon");
        marker.style.left = pos + 'px';
        marker.style.display = 'block';
    }

    /**
     * Renders the compass
     * @returns {undefined}
     */
    render() {
        let precision = Math.pow(10, 3);

        let realAngle = this.angle % 360;
        let offset = Math.floor((-1 * realAngle / 90 * this.WIDTH) * precision) / precision;

        if (offset == this.lastOffset) {
            return;
        }

        let inset = Math.floor((-4 * this.WIDTH) * precision) / precision;
        let center = 180;

        document.getElementById("compass").classList.remove("right");
        document.getElementById("compass").classList.remove("left");

        let delta = this.angle - this.markerPos + 45;

        if (this.markerPos != null){
            if (Math.abs(Math.min(delta, 360 - delta)) > 45)
            {
                if (delta > 180 || delta < 0) {
                    document.getElementById("compass").classList.add("right");
                } else {
                    document.getElementById("compass").classList.add("left");
                }
            }
        }

        // Switching north segment when rolling over
        if (realAngle > center && this.leftside) {
            //Marker must handle the roll over too... Switching the positions is done by offset
            if (this.markerPos != null){
                if (this.markerPos < 90) {
                    this.renderMarker(this.markerPos, 360);
                } else if (this.markerPos > 270) {
                    this.renderMarker(this.markerPos, 0);
                }
            }

            this.slider.firstElementChild.style.transform = `translateX(${-inset}px)`;
            this.slider.lastElementChild.style.transform = ``;
            this.leftside = false;
        } else if (realAngle < center && !this.leftside) {
            //Marker must handle the roll over too... Switching the positions is done by offset
            if (this.markerPos != null){
                if (this.markerPos < 90) {
                    this.renderMarker(this.markerPos, 0);
                } else if (this.markerPos > 270) {
                    this.renderMarker(this.markerPos, -360);
                }
            }

            this.slider.lastElementChild.style.transform = `translateX(${inset}px)`;
            this.slider.firstElementChild.style.transform = ``;
            this.leftside = true;
        }

        this.slider.style.transform = `translateX(${offset}px)`;

        this.lastOffset = offset;
    }
}

