/**
 * This class manages settings and applying them. 
 */
export class Settings {
    /**
     * Constructor sets the default values of member variables. Also initializes the event listeners
     * for range sliders and the save event.
     * @param {*} caller Caller object
     */
    constructor(caller) {
        this.caller = caller;
        this.setDefaults();
        this.init();

        let elements = document.querySelectorAll(".slider input[type=range]")
        elements.forEach(el => {    
            el.addEventListener("input", (e) =>{
                e.currentTarget.closest(".slider").querySelector("input[type=number]").value = e.currentTarget.value;
            });
        });

        elements = document.querySelectorAll(".slider input[type=number]")
        elements.forEach(el => {
            el.addEventListener("change", (e) => {
                e.currentTarget.closest(".slider").querySelector("input[type=range]").value = e.currentTarget.value;
            });
        });

        document.getElementById("save").addEventListener("click", (e) =>{
            this.saveSettings();
            document.querySelector("body").classList.remove("hide-controls");
            document.getElementById("close-all").classList.add("hide");
            document.getElementById("settings").classList.add("hide");
        });
    }

    /**
     * If not defined yet, set the default values of settings
     * @returns {undefined}
     */
    setDefaults(){
        if (!window.localStorage.getItem("geoMaxAge")) {
            window.localStorage.setItem('geoMaxAge', 1);
        }

        if (!window.localStorage.getItem("videoQuality")) {
            window.localStorage.setItem('videoQuality', 1);
        }
    }

    /**
     * Sets the saved values to the inputs. 
     * @returns {undefined}
     */
    init(){
        let elements = document.querySelectorAll(".slider input[type=range]")
        elements.forEach(el => {
            let value = window.localStorage.getItem(el.name);
            el.value = value;
            el.closest(".slider").querySelector("input[type=number]").value = value;
        });
    }

    /**
     * Saves settings and does all actions needed to apply them.
     * @returns {undefined}
     */
    saveSettings(){
        let elements = document.querySelectorAll(".slider input[type=range]")
        elements.forEach(el => {
            if (window.localStorage.getItem(el.name) != el.value)
            {
                window.localStorage.setItem(el.name, el.value);
            } else {
                return;
            }

            console.info(`Updating ${el.name} to ${el.value}`);

            switch (el.name) {
                case "videoQuality":
                    this.caller.initVideo();        
                    break;

                case "geoMaxAge":
                    this.caller.positionService.reinitGeolocationWatcher();
                    break;

                default:
                    break;
            }


        });
    }

}

/**
 * Computes mean from array of numbers
 * @returns {Number} Mean
 */
Array.prototype.mean = function () {
    if (this.length == 0) {
        return NaN;
    }

    return this.reduce((sum, value) => sum + value) / this.length;
}

/**
 * Computes standard deviation from array of numbers
 * @returns {Number} Standard deviation
 */
Array.prototype.standardDeviation = function () {
    if (this.length == 0) {
        return NaN;
    }

    return Math.sqrt((this.map(value => (value - this.mean()) ** 2)).mean())
};

/**
 * Computes median from array of numbers
 * @returns {Number} Median
 */
Array.prototype.median = function () {
    if (this.length == 0) {
        return NaN;
    }

    let arr = this.slice(0).sort((a, b) => a - b); // slice for quick clone
    let half = ~~(arr.length / 2);

    return (arr.length % 2) ? arr[half] : (arr[half - 1] + arr[half]) / 2.0;
}

/**
 * Iglewicz and Hoaglin method (median absolute deviation method)
 * Values with a Z-score > 3.5 are considered potential outliers
 * Based on https://github.com/alyssaq/stats-analysis
 * @param {Number[]} arr Array of numbers with outliers
 * @returns {Number[]} Array of numbers without outliers
 */
export function MAD(arr) {
    let threshold = 3.5;

    let median = arr.median();
    let MAD = (arr.map((e) => Math.abs(e - median))).median();

    /**
     * Treshold check for MAD method
     * @param {Number} e Value
     * @returns {Boolean} Whether value is outlier
     */
    let check = (e) => Math.abs((0.6745 * (e - median)) / MAD) <= threshold;

    let res = arr.filter(check);

    return res;
}

/**
 * Wizard class starts and handles the wizards that are displayed on first start and when location fix is needed
 */
export class Wizard{
    /**
     * Initializes the wizard, if run once is true, checks whether wizard already ran using WebStorage.
     * @param {String} selector string selector
     * @param {Boolean} runOnce whether to run wizard only once
     * @param {Object} caller caller object
     */
    constructor(selector, runOnce, caller){
        this.selector = selector;
        this.baseElement = document.querySelector(selector);
        this.runOnce = runOnce;
        this.caller = caller;
        if (runOnce && window.localStorage.getItem(`wizard${selector}`)){
            this.wasRun = false;
            return;
        }

        this.wasRun = true;
        this.baseElement.classList.remove("hide");

        let elements = document.querySelectorAll(`${selector} .btn-next`);
        this.slide = 0;
        let wizard = this;

        document.querySelector("body").classList.add("hide-controls");

        elements.forEach(element => {
            element.addEventListener("click", function (e) {
                e.preventDefault();

                let href = this.getAttribute("href");
                let slide = this.closest(".slide");    
                let action = slide.getAttribute("data-action");

                switch (action) {
                    case "camera":
                        caller.initVideo();
                        break;

                    case "location":
                        caller.positionService.initGeolocationWatcher();
                        break;

                    case "calibration":
                        caller.startCalibration();
                        break;

                    case "calibration_end":
                        caller.endCalibration();
                        break;

                    default:
                        break;
                }

                if (href == "#finish" || href == "./")
                {
                    wizard.finished();
                    return false;
                }     

                wizard.slide++;

                let nextSlide = wizard.baseElement.querySelector(`.slide-nr${wizard.slide}`);       

                //wizard.vibrateAlert(0);



                slide.classList.add("hide");
                nextSlide.classList.remove("hide");
            });
        });
    }

    /**
     * Hides the wizard and if it should be run only once, writes true to WebStorage.
     * @returns {undefined}
     */
    finished(){
        if (this.runOnce){
            window.localStorage.setItem(`wizard${this.selector}`, true);
        }

        document.querySelector("body").classList.remove("hide-controls");
        this.baseElement.classList.add("hide");
    }

    /**
     * Hides all wizards
     * @returns {undefined}
     */
    static hideAllWizards(){
        const elements = document.querySelectorAll(".wizard");
        elements.forEach(el => {
            if (!el.classList.contains("hide")){
                el.classList.add("hide");
            }
        });
    }

    /**
     * Vibrates phone to notify user of location fix
     * @param {Number} count Couns how many vibrations took place
     * @returns {undefined}
     */
    static vibrateAlert(count) {
        if (count == 4) {
            return;
        }

        window.navigator.vibrate(400);

        setTimeout(() => {
            this.vibrateAlert(count + 1);
        }, 600);
    }
}
